mapper.select:
select n.id as id, n.uuid as uuid, n.version as version, n.type_qname_id as type_qname_id, n.store_id as store_id, #{storeProtocol} as protocol, #{storeIdentifier} as identifier, q.local_name as type_name, ns.uri as type_namespace, acl.acl_change_set as acl_changeset_id from alf_node n
left outer join alf_access_control_list acl on n.acl_id = acl.id join alf_qname q on q.id=n.type_qname_id join alf_namespace ns on ns.id=q.ns_id
and ('{' || ns.uri || '}' || q.local_name) in
#{type}
join alf_qname qm on qm.id=n.type_qname_id join alf_node_properties pm on pm.node_id=n.id and pm.qname_id=qm.id join alf_content_data dm on dm.id=pm.long_value join alf_mimetype m on dm.content_mimetype_id = m.id and m.mimetype_str in
#{mime}
where
n.id in ( SELECT n.id FROM alf_node n
JOIN alf_node_aspects a${index} ON a${index}.node_id = n.id JOIN alf_qname q${index} ON q${index}.id = a${index}.qname_id JOIN alf_namespace ns${index} ON ns${index}.id = q${index}.ns_id
WHERE
('{' || ns${index}.uri || '}' || q${index}.local_name) = #{aspect}
) and
n.id not in ( select n.id from alf_node n join alf_node_properties np on np.node_id=n.id join alf_qname qnp on qnp.id=np.qname_id and qnp.local_name='name' and
np.string_value like ('%' || #{extension})
) and
n.store_id = #{storeId} and acl.acl_change_set > #{minId} and acl.acl_change_set <= #{maxId} order by n.transaction_id ASC

select n.id as id, n.uuid as uuid, n.version as version, n.type_qname_id as type_qname_id, n.store_id as store_id, #{storeProtocol} as protocol, #{storeIdentifier} as identifier, q.local_name as type_name, ns.uri as type_namespace, n.transaction_id as txn_id from alf_node n
join alf_qname q on q.id=n.type_qname_id join alf_namespace ns on ns.id=q.ns_id
and ('{' || ns.uri || '}' || q.local_name) in
#{type}
join alf_qname qm on qm.id=n.type_qname_id join alf_node_properties pm on pm.node_id=n.id and pm.qname_id=qm.id join alf_content_data dm on dm.id=pm.long_value join alf_mimetype m on dm.content_mimetype_id = m.id and m.mimetype_str in
#{mime}
where
n.id in ( SELECT n.id FROM alf_node n
JOIN alf_node_aspects a${index} ON a${index}.node_id = n.id JOIN alf_qname q${index} ON q${index}.id = a${index}.qname_id JOIN alf_namespace ns${index} ON ns${index}.id = q${index}.ns_id
WHERE
('{' || ns${index}.uri || '}' || q${index}.local_name) = #{aspect}
) and
n.id not in ( select n.id from alf_node n join alf_node_properties np on np.node_id=n.id join alf_qname qnp on qnp.id=np.qname_id and qnp.local_name='name' and
np.string_value like ('%' || #{extension})
) and
n.store_id = #{storeId} and n.transaction_id > #{minId} and n.transaction_id <= #{maxId} order by n.transaction_id ASC

select node.id as id, node.uuid as uuid, node.version as version, node.type_qname_id as type_qname_id, node.store_id as store_id, #{storeProtocol} as protocol, #{storeIdentifier} as identifier, qname.local_name as type_name, ns.uri as type_namespace, np.string_value as node_name, node.transaction_id as txn_id
from alf_node node left outer join alf_qname qname on node.type_qname_id = qname.id left outer join alf_namespace ns on qname.ns_id = ns.id
join alf_node_properties np on np.node_id=node.id join alf_qname qnp on qnp.id=np.qname_id and qnp.local_name in ('name','originalId') where node.store_id = #{storeId} and node.uuid = #{uuid}
